  useEffect(() => {
    setMounted(true)
  }, [])

  // Function to repeat the ASCII art to fill the screen
  const createRepeatingPattern = (asciiArt: string) => {
    const rows = []
    for (let i = 0; i < 1; i++) {
      rows.push(
        <pre key={i} className="ascii-art-block">
          {asciiArt}
        </pre>,
      )
    }
    return rows
  }

  if (!mounted) return null

  return (
    <div className="ascii-background">
      <div className={`ascii-pattern ${theme === "light" ? "light-pattern" : "dark-pattern"}`}>
        {theme === "light" ? createRepeatingPattern(lightAsciiArt) : createRepeatingPattern(darkAsciiArt)}
      </div>
    </div>
  )
